import {React, useState} from "react";
import Device from "./components/Device";
import DeviceDetails from "./components/DeviceDetails";

function App() {
  const [showDetails, setShowDetails] = useState(false)
  const fetchedSmartDevices = [
    {
      type: "bulb",
      id: "sd1",
      name: "Żarówka Kuchnia",
      connectionState: "connected"
    },
    {
      type: "outlet",
      id: "sd2",
      name: "Gniazdko Sypialnia",
      connectionState: "disconnected"
    },
    {
      type: "temperatureSensor",
      id: "sd3",
      name: "Termostat",
      connectionState: "poorConnection"
    }
  ]
  const smartDeviceOutlet = {
    type: "outlet",
    id: "sd2",
    name: "Gniazdko Sypialnia",
    connectionState: "disconnected",
    isTurnedOn: true,
    powerConsumption: "2300W"
  }
  const smartDeviceTemp = {
    type: "temperatureSensor",
    id: "sd2",
    name: "Termostat",
    connectionState: "poorConnection",
    temperature: 21
  }
  const smartDeviceBulb = {
    type: "bulb",
    id: "sd2",
    name: "Żarówka Kuchnia",
    connectionState: "connected",
    isTurnedOn: true,
    brightness: 58,
    color: "rgb(103, 216, 81)"
  }

  return (
    <div className="App">
      <h1 id="main-header">myHome</h1>
      <div id="devices-grid">
        {fetchedSmartDevices.map((device)=>{
          return <Device 
            device = {device}
            setShowDetails={setShowDetails}
            />
        })}
        {/* Kod jest pisany pod większą liczbę urządzeń, można odkomentować poniższą zawartość, żeby od razu zobaczyć jak to wygląda. */}
        {/* <Device device = {fetchedSmartDevices[0]}/>
        <Device device = {fetchedSmartDevices[0]}/>
        <Device device = {fetchedSmartDevices[0]}/>
        <Device device = {fetchedSmartDevices[0]}/>
        <Device device = {fetchedSmartDevices[0]}/>
        <Device device = {fetchedSmartDevices[0]}/>
        <Device device = {fetchedSmartDevices[0]}/>
        <Device device = {fetchedSmartDevices[0]}/>
        <Device device = {fetchedSmartDevices[0]}/> */}
      </div>
      <DeviceDetails 
        device = {smartDeviceBulb} 
        showDetails={showDetails}
        setShowDetails={setShowDetails}/>
    </div>
  );
}

export default App;
