import { useEffect } from "react"

const Device = (props) => {
  const {device, setDetailsToBeShown} = props

// Ze względu na mało "user-friendly" nazwy typów urządzeń i ich stanów połączeń 
// zdecydowałem się odwzorować je odpowiednio symbolami i kolorami, zamiast opisywać, 
// bo user pewnie wolałby zobaczyć odwzorowanie graficzne niż np. "temperatureSensor" pisane camelCasem :)
// Mam nadzieję że to nie problem.

  const handleShowDetails = () =>{
    setStyle({display: "block"})
    setDetailsToBeShown(device)
    // setShowDetails(true)
  }
  useEffect(()=>{
    setDetailsToBeShown(device)
  },[device.connectionState, 
    device.isTurnedOn, 
    device.brightness,  
    device.color,
    device.powerConsumption,
    device.temperature,
  ])

    return (
      <div 
        id={device.id} 
        className={"device " + device.connectionState}
        onClick={()=>{handleShowDetails()}}>
        <img src={require("../styles/images/"+device.type+".png")} />
        <h3>{device.name}</h3>
      </div>
    );
  }
  
  export default Device;
