const DeviceDetails = (props) => {

    const {device} = props
    return(
        <div id="device-details">
            <p><img src={require("../styles/images/"+device.type+".png")} />NAZWA</p>
            <p>{device.connectionState
                .replace(/([A-Z])/g, ' $1')
                .replace(/^./, (str) => {
                return str.toUpperCase();
                })}</p>
            <div>
            {device.connectionState !== "disconnected" &&
                <div>
                    {device.isTurnedOn && <p>WŁĄCZONY</p>}
                    {device.brightness && <p>JASNOŚĆ: {device.brightness}/100</p>}
                    {device.color && <p>KOLOR:</p>}
                    {device.powerConsumption && <p>POBÓR ENERGII: {device.powerConsumption}</p>}
                    {device.temperature && <p>TEMPERATURA: {device.temperature}C</p>}
                </div>}
            </div>
        </div>
    )
}
export default DeviceDetails
