    <div className="App">
      <h1 id="main-header">myHome</h1>
      {smartDevices && <div id="devices-grid">
        {smartDevices.map((device)=>{
          return <Device 
            key={device.id}
            device = {device}
            setDetailsToBeShown={setDetailsToBeShown}
            setShowDetails={setShowDetails}
            />
