import bulb from "../styles/images/bulb.png"
import outlet from "../styles/images/outlet.png"
import temperatureSensor from "../styles/images/temperatureSensor.png"

const Device = (props) => {

  const {device} = props

    return (
      <div id={device.id} className={"device " + device.connectionState}>
        <p>{device.name}</p>
        <img src={
          device.type === "bulb" ? bulb : 
          device.type === "outlet" ? outlet : 
          temperatureSensor} 
          alt={device.type} />
      </div>
    );
  }
  
  export default Device;
