import {React, useEffect, useState} from "react";
import Device from "./components/Device";
import DeviceDetails from "./components/DeviceDetails";
import interact from 'https://cdn.interactjs.io/v1.10.11/interactjs/index.js'

function App() {
  const [showDetails, setShowDetails] = useState(false)
  const [smartDevices, setSmartDevices] = useState()
  const [detailsToBeShown, setDetailsToBeShown] = useState()

  interact('#device-details')
  .resizable({
    edges: { left: true, right: true, bottom: true, top: true },

    listeners: {
      move (event) {
        var target = event.target
        var x = (parseFloat(target.getAttribute('data-x')) || 0)
        var y = (parseFloat(target.getAttribute('data-y')) || 0)

        target.style.width = event.rect.width + 'px'
        target.style.height = event.rect.height + 'px'

        x += event.deltaRect.left
        y += event.deltaRect.top

        target.style.transform = 'translate(' + x + 'px,' + y + 'px)'

        target.setAttribute('data-x', x)
        target.setAttribute('data-y', y)
      }
    },
    modifiers: [
      interact.modifiers.restrictEdges({
        outer: ''
      }),

      interact.modifiers.restrictSize({
        min: { width: 250, height: 300 }
      })
    ],

    inertia: true
  })
  .draggable({
    inertia: true,
    modifiers: [
      interact.modifiers.restrictRect({
        restriction: 'parent',
        endOnly: true
      })
    ],
    autoScroll: false,

    listeners: {
      move: dragMoveListener,
    }
  })

function dragMoveListener (event) {
  var target = event.target
  var x = (parseFloat(target.getAttribute('data-x')) || 0) + event.dx
  var y = (parseFloat(target.getAttribute('data-y')) || 0) + event.dy

  target.style.transform = 'translate(' + x + 'px, ' + y + 'px)'

  target.setAttribute('data-x', x)
  target.setAttribute('data-y', y)
}

  useEffect(()=>{
    setInterval(() => {
      fetch("http://localhost:3000/devices")
      .then(resp => resp.json())
      .then((data)=>{
        if(data!==smartDevices){
          setSmartDevices(data)
        }
      })
    }, 1000);
  },[])

  return (
    <div className="App">
      <h1 id="main-header">myHome</h1>
      {smartDevices && <div id="devices-grid">
        {smartDevices.map((device)=>{
          return <Device 
            key={device.id}
            device = {device}
            setDetailsToBeShown={setDetailsToBeShown}
            setShowDetails={setShowDetails}
            />
        })}
        {/* Kod jest pisany pod większą liczbę urządzeń, można odkomentować poniższą zawartość, żeby zobaczyć jak to będzie wyglądać. */}
        {/* <Device device = {smartDevices[0]}/>
        <Device device = {smartDevices[0]}/>
        <Device device = {smartDevices[0]}/>
        <Device device = {smartDevices[0]}/>
        <Device device = {smartDevices[0]}/>
        <Device device = {smartDevices[0]}/>
        <Device device = {smartDevices[0]}/>
        <Device device = {smartDevices[0]}/>
        <Device device = {smartDevices[0]}/> */}
      </div>}
      {showDetails && <DeviceDetails 
        device = {detailsToBeShown}
        setShowDetails={setShowDetails}/>}
    </div>
  );
}

export default App;
