import bulbImg from "../styles/images/bulb.png"
import outletImg from "../styles/images/outlet.png"
import temperatureSensorImg from "../styles/images/temperatureSensor.png"

const Device = (props) => {
  const {device} = props

  const imageName = () => {
    return require("../styles/images/temperatureSensor.png")
    // switch (device.type){
    //   case "bulb": return bulbImg
    //   case "outlet": return outletImg
    //   case "temperatureSensor": return temperatureSensorImg
    // }
  }

    return (
      <div id={device.id} className={"device " + device.connectionState}>
        <p>{device.name}</p>
        <img src={imageName()} />
      </div>
    );
  }
  
  export default Device;
